import React, { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Label, Input } from 'reactstrap';

const ModalButton = (props) => {

  const [modal, setModal] = useState(false);

  const toggle = () => setModal(!modal);

  function postlink(setList){
    setList({
      url: event.target.value
    })
  }


  return (
    <div>
      <Button color="danger" onClick={toggle}>{props.buttonLabel}</Button>
      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader>Enter the new link</ModalHeader>
        <ModalBody>
          <FormGroup id="myform">
            <Label for="exampleUrl">Link</Label>
            <Input
              type="url"
              name="url"
              placeholder="url"
              form="myform"
            />
          </FormGroup>
        </ModalBody>
        <ModalFooter>
          <Button color="primary" type="submit" onClick={toggle}>Post</Button>{' '}
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
    </div>
  );
}

export default ModalButton;
